mapper.select:
select

from blog
where is_deleted=0

	AND (blog_title like CONCAT('%',#{keyword},'%' ) or
	blog_category_name
	like CONCAT('%',#{keyword},'%' ))


	AND blog_status = #{blogStatus}


	AND blog_category_id = #{blogCategoryId}

order by id desc

	limit #{start},#{limit}

select count(*) from blog
where is_deleted=0

	AND (blog_title like CONCAT('%',#{keyword},'%' ) or blog_category_name
	like CONCAT('%',#{keyword},'%' ))


	AND blog_status = #{blogStatus}


	AND blog_category_id = #{blogCategoryId}

mapper.update:
update blog
set is_deleted=1 where id in

	#{id}
